////////////////////////////////////////////////////////////////////////////////
// ShBadge

// Includes
#include "ShBadge.h"
#include "RsUtil.h"

////////////////////////////////////////////////////////////////////////////////
// statics

// Public functions
BtQueue<ShBadgeAction, 128> ShBadge::m_actions;

////////////////////////////////////////////////////////////////////////////////
// PushAction

//static
void ShBadge::PushAction( ShBadgeAction action )
{
    m_actions.Push( action );
}

////////////////////////////////////////////////////////////////////////////////
// GetNumItems

//static
BtU32 ShBadge::GetNumItems()
{
    return m_actions.GetItemCount();
}

////////////////////////////////////////////////////////////////////////////////
// PopAction

//static
ShBadgeAction ShBadge::PopAction()
{
    ShBadgeAction action;
    action = m_actions.Pop();
    return action;
}

////////////////////////////////////////////////////////////////////////////////
// PeekAction

//static
ShBadgeAction ShBadge::PeekAction()
{
	ShBadgeAction action;
	action = m_actions.Peek();
	return action;
}

