////////////////////////////////////////////////////////////////////////////////
// ShDialog

// Includes
#include "ShDialog.h"

////////////////////////////////////////////////////////////////////////////////
// statics

//static

// Public functions
BtQueue<ShDialogActionType, 128> ShDialog::m_actions;

////////////////////////////////////////////////////////////////////////////////
// PushAction

//static
void ShDialog::PushAction( ShDialogActionType cameraAction )
{
	if( m_actions.GetItemCount() == 0 )
	{
		m_actions.Push( cameraAction );
	}
}

////////////////////////////////////////////////////////////////////////////////
// GetNumItems

//static
BtU32 ShDialog::GetNumItems()
{
    return m_actions.GetItemCount();
}

////////////////////////////////////////////////////////////////////////////////
// PopAction

//static
ShDialogActionType ShDialog::PopAction()
{
    ShDialogActionType action;
    action = m_actions.Pop();
    return action;
}

