////////////////////////////////////////////////////////////////////////////////
// ShMusic

// Includes
#include "ShMusic.h"
#include "RsUtil.h"

////////////////////////////////////////////////////////////////////////////////
// statics

//static

// Public functions
BtQueue<ShMusicAction, 128> ShMusic::m_actions;
BtQueue<ShMusicAction, 128> ShMusic::m_alerts;

////////////////////////////////////////////////////////////////////////////////
// PushAction

//static
void ShMusic::PushAction( ShMusicAction action )
{
    m_actions.Push( action );
}

////////////////////////////////////////////////////////////////////////////////
// PushAlert

//static
void ShMusic::PushAlert( ShMusicAction action )
{
    m_alerts.Push( action );
}

////////////////////////////////////////////////////////////////////////////////
// GetNumItems

//static
BtU32 ShMusic::GetNumItems()
{
    return m_actions.GetItemCount();
}

////////////////////////////////////////////////////////////////////////////////
// GetNumAlerts

//static
BtU32 ShMusic::GetNumAlerts()
{
    return m_alerts.GetItemCount();
}

////////////////////////////////////////////////////////////////////////////////
// PopAlert

//static
ShMusicAction ShMusic::PopAlert()
{
    ShMusicAction action;
    action = m_alerts.Pop();
    return action;
}

////////////////////////////////////////////////////////////////////////////////
// PopAction

//static
ShMusicAction ShMusic::PopAction()
{
    ShMusicAction action;
    action = m_actions.Pop();
    return action;
}

